package pycasm.sym;import java.util.HashMap;import pycasm.sym.NativeDefinition;import static pycasm.sym.PycasmSymbol.PycasmType;public class PycasmRootScope extends PycasmScope {	private String pythonDirective(String version) {		HashMap<String,String> v2c = new HashMap<String,String>() {{			put("2.5", "b3f2");			put("3.2","6c0c");		}};		return v2c.get(version)+"0d0a";	}		public PycasmRootScope() {		super();		PycasmSymbol pop_top = new PycasmSymbol("pop_top", PycasmType.GEN, this, new NativeDefinition("01"));		this.putMember("pop_top", pop_top);		NativeDefinition nd = new NativeDefinition(new NativeDefinition.StringFunction() {			public String function(String param) {				return pythonDirective(param);			}		});		PycasmSymbol python = new PycasmSymbol(".python", PycasmType.DIR, this, nd);		this.putMember(".python", python);	}}